import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
          await import("@replit/vite-plugin-dev-banner").then((m) =>
            m.devBanner(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

contracts/BlueCarbon.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";

contract BlueCarbon is ERC20, AccessControl, ERC20Burnable {
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant NGO_ROLE = keccak256("NGO_ROLE");

    // Events
    event ProjectVerified(string indexed projectId, address indexed ngo, uint256 creditsIssued);
    event CreditsRetired(address indexed owner, uint256 amount, string reason);

    // Project tracking
    mapping(string => bool) public verifiedProjects;
    mapping(string => uint256) public projectCredits;

    // Statistics
    uint256 public totalProjectsVerified;
    uint256 public totalCreditsIssued;
    uint256 public totalCreditsRetired;

    constructor() ERC20("BlueCarbon Credit", "BC") {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
    }

    modifier onlyAdmin() {
        require(hasRole(ADMIN_ROLE, msg.sender), "BlueCarbon: caller is not an admin");
        _;
    }

    modifier onlyNGO() {
        require(hasRole(NGO_ROLE, msg.sender), "BlueCarbon: caller is not an NGO");
        _;
    }

    function grantNGORole(address ngo) external onlyAdmin {
        _grantRole(NGO_ROLE, ngo);
    }

    function revokeNGORole(address ngo) external onlyAdmin {
        _revokeRole(NGO_ROLE, ngo);
    }

    function grantAdminRole(address admin) external onlyAdmin {
        _grantRole(ADMIN_ROLE, admin);
    }

    function verifyProjectAndMint(
        string calldata projectId,
        address ngoAddress,
        uint256 creditsAmount
    ) external onlyAdmin {
        require(!verifiedProjects[projectId], "BlueCarbon: project already verified");
        require(creditsAmount > 0, "BlueCarbon: credits amount must be greater than 0");
        require(ngoAddress != address(0), "BlueCarbon: invalid NGO address");

        verifiedProjects[projectId] = true;
        projectCredits[projectId] = creditsAmount;
        totalProjectsVerified++;
        totalCreditsIssued += creditsAmount;

        _mint(ngoAddress, creditsAmount * 10**decimals());

        emit ProjectVerified(projectId, ngoAddress, creditsAmount);
    }

    function retireCredits(uint256 amount, string calldata reason) external {
        require(amount > 0, "BlueCarbon: amount must be greater than 0");
        require(balanceOf(msg.sender) >= amount, "BlueCarbon: insufficient balance");

        totalCreditsRetired += amount;
        _burn(msg.sender, amount);

        emit CreditsRetired(msg.sender, amount, reason);
    }

    function burn(uint256 amount) public override {
        totalCreditsRetired += amount;
        super.burn(amount);
    }

    // View functions
    function isProjectVerified(string calldata projectId) external view returns (bool) {
        return verifiedProjects[projectId];
    }

    function getProjectCredits(string calldata projectId) external view returns (uint256) {
        return projectCredits[projectId];
    }

    function getStats() external view returns (
        uint256 _totalProjects,
        uint256 _totalIssued,
        uint256 _totalRetired,
        uint256 _circulating
    ) {
        return (
            totalProjectsVerified,
            totalCreditsIssued,
            totalCreditsRetired,
            totalSupply()
        );
    }

    // Required override for AccessControl
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC20, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}

contracts/CarbonRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./BlueCarbon.sol";
import "./NFTCertificate.sol";

/**
 * @title CarbonRegistry
 * @dev Registry contract for managing Blue Carbon MRV projects with double-counting prevention
 * Coordinates between BlueCarbon ERC20 tokens and NFT certificates
 */
contract CarbonRegistry is Ownable, ReentrancyGuard {

    BlueCarbon public immutable blueCarbonToken;
    NFTCertificate public immutable nftCertificate;

    // Project status enum
    enum ProjectStatus { Pending, Verified, Rejected, Locked }

    // Project registry structure
    struct RegisteredProject {
        string projectId;
        address submitter;
        uint256 areaHectares;
        string location;
        bytes32 locationHash;
        bytes32 dataHash;
        ProjectStatus status;
        uint256 submissionTime;
        uint256 verificationTime;
        address verifier;
        uint256 carbonCredits;
        uint256 nftTokenId;
        bool nftMinted;
    }

    // Mappings for double-counting prevention
    mapping(string => RegisteredProject) public projects;
    mapping(bytes32 => bool) public usedLocationHashes;
    mapping(bytes32 => bool) public usedDataHashes;
    mapping(address => string[]) public submitterProjects;

    // Verification requirements
    mapping(address => bool) public authorizedVerifiers;

    // Statistics
    uint256 public totalRegisteredProjects;
    uint256 public totalVerifiedProjects;
    uint256 public totalRejectedProjects;

    // Events
    event ProjectRegistered(
        string indexed projectId,
        address indexed submitter,
        bytes32 locationHash,
        bytes32 dataHash
    );

    event ProjectVerified(
        string indexed projectId,
        address indexed verifier,
        uint256 carbonCredits,
        uint256 nftTokenId
    );

    event ProjectRejected(
        string indexed projectId,
        address indexed verifier,
        string reason
    );

    event DuplicateAttemptDetected(
        string indexed projectId,
        address indexed submitter,
        string conflictType
    );

    event VerifierAuthorized(address indexed verifier);
    event VerifierRevoked(address indexed verifier);

    constructor(address _blueCarbonToken, address _nftCertificate) {
        blueCarbonToken = BlueCarbon(_blueCarbonToken);
        nftCertificate = NFTCertificate(_nftCertificate);
        authorizedVerifiers[msg.sender] = true;
    }

    modifier onlyAuthorizedVerifier() {
        require(authorizedVerifiers[msg.sender], "CarbonRegistry: Not an authorized verifier");
        _;
    }

    modifier projectExists(string memory projectId) {
        require(bytes(projects[projectId].projectId).length > 0, "CarbonRegistry: Project does not exist");
        _;
    }

    /**
     * @dev Register a new project with double-counting prevention
     * @param projectId Unique project identifier
     * @param areaHectares Area of the plantation in hectares
     * @param location Geographic location description
     * @param latitude Latitude coordinates (as string to avoid precision loss)
     * @param longitude Longitude coordinates (as string to avoid precision loss)
     * @param ipfsDataHash IPFS hash of project documentation
     */
    function registerProject(
        string memory projectId,
        uint256 areaHectares,
        string memory location,
        string memory latitude,
        string memory longitude,
        string memory ipfsDataHash
    ) external nonReentrant {
        require(bytes(projectId).length > 0, "CarbonRegistry: Empty project ID");
        require(areaHectares > 0, "CarbonRegistry: Area must be greater than 0");
        require(bytes(projects[projectId].projectId).length == 0, "CarbonRegistry: Project ID already exists");

        // Create hashes for double-counting prevention
        bytes32 locationHash = keccak256(abi.encodePacked(latitude, longitude, areaHectares));
        bytes32 dataHash = keccak256(abi.encodePacked(projectId, ipfsDataHash, msg.sender));

        // Check for duplicate locations (with tolerance for small variations)
        require(!usedLocationHashes[locationHash], "CarbonRegistry: Location already registered");

        // Check for duplicate data submissions
        require(!usedDataHashes[dataHash], "CarbonRegistry: Data already submitted");

        // Register the project
        projects[projectId] = RegisteredProject({
            projectId: projectId,
            submitter: msg.sender,
            areaHectares: areaHectares,
            location: location,
            locationHash: locationHash,
            dataHash: dataHash,
            status: ProjectStatus.Pending,
            submissionTime: block.timestamp,
            verificationTime: 0,
            verifier: address(0),
            carbonCredits: 0,
            nftTokenId: 0,
            nftMinted: false
        });

        // Mark hashes as used
        usedLocationHashes[locationHash] = true;
        usedDataHashes[dataHash] = true;

        // Add to submitter's project list
        submitterProjects[msg.sender].push(projectId);
        totalRegisteredProjects++;

        emit ProjectRegistered(projectId, msg.sender, locationHash, dataHash);
    }

    /**
     * @dev Verify a project and mint carbon credits + NFT certificate
     * @param projectId Project to verify
     * @param carbonCredits Amount of carbon credits to award
     * @param metadataURI IPFS URI for NFT metadata
     * @param ecosystem Type of ecosystem ("mangrove", "seagrass", "saltmarsh")
     */
    function verifyProjectAndMint(
        string memory projectId,
        uint256 carbonCredits,
        string memory metadataURI,
        string memory ecosystem
    ) external onlyAuthorizedVerifier projectExists(projectId) nonReentrant {
        RegisteredProject storage project = projects[projectId];
        require(project.status == ProjectStatus.Pending, "CarbonRegistry: Project not pending verification");
        require(carbonCredits > 0, "CarbonRegistry: Carbon credits must be greater than 0");

        // Update project status
        project.status = ProjectStatus.Verified;
        project.verificationTime = block.timestamp;
        project.verifier = msg.sender;
        project.carbonCredits = carbonCredits;

        // Mint carbon credits to the project submitter
        blueCarbonToken.verifyProjectAndMint(projectId, project.submitter, carbonCredits);

        // Mint NFT certificate
        uint256 nftTokenId = nftCertificate.mintCertificate(
            projectId,
            project.location,
            project.areaHectares,
            carbonCredits,
            ecosystem,
            project.submitter,
            metadataURI
        );

        project.nftTokenId = nftTokenId;
        project.nftMinted = true;

        totalVerifiedProjects++;

        emit ProjectVerified(projectId, msg.sender, carbonCredits, nftTokenId);
    }

    /**
     * @dev Reject a project
     * @param projectId Project to reject
     * @param reason Reason for rejection
     */
    function rejectProject(
        string memory projectId,
        string memory reason
    ) external onlyAuthorizedVerifier projectExists(projectId) {
        RegisteredProject storage project = projects[projectId];
        require(project.status == ProjectStatus.Pending, "CarbonRegistry: Project not pending verification");

        project.status = ProjectStatus.Rejected;
        project.verificationTime = block.timestamp;
        project.verifier = msg.sender;

        totalRejectedProjects++;

        emit ProjectRejected(projectId, msg.sender, reason);
    }

    /**
     * @dev Check if a location is already registered (double-counting prevention)
     * @param latitude Latitude coordinates
     * @param longitude Longitude coordinates
     * @param areaHectares Area in hectares
     * @return bool True if location is already used
     */
    function isLocationUsed(
        string memory latitude,
        string memory longitude,
        uint256 areaHectares
    ) external view returns (bool) {
        bytes32 locationHash = keccak256(abi.encodePacked(latitude, longitude, areaHectares));
        return usedLocationHashes[locationHash];
    }

    /**
     * @dev Get project details
     * @param projectId Project identifier
     * @return RegisteredProject struct
     */
    function getProject(string memory projectId) external view returns (RegisteredProject memory) {
        return projects[projectId];
    }

    /**
     * @dev Get all projects submitted by an address
     * @param submitter Address to query
     * @return string[] Array of project IDs
     */
    function getProjectsBySubmitter(address submitter) external view returns (string[] memory) {
        return submitterProjects[submitter];
    }

    /**
     * @dev Authorize a verifier
     * @param verifier Address to authorize
     */
    function authorizeVerifier(address verifier) external onlyOwner {
        require(verifier != address(0), "CarbonRegistry: Invalid verifier address");
        authorizedVerifiers[verifier] = true;
        emit VerifierAuthorized(verifier);
    }

    /**
     * @dev Revoke verifier authorization
     * @param verifier Address to revoke
     */
    function revokeVerifier(address verifier) external onlyOwner {
        authorizedVerifiers[verifier] = false;
        emit VerifierRevoked(verifier);
    }

    /**
     * @dev Check if an address is an authorized verifier
     * @param verifier Address to check
     * @return bool True if authorized
     */
    function isAuthorizedVerifier(address verifier) external view returns (bool) {
        return authorizedVerifiers[verifier];
    }

    /**
     * @dev Get registry statistics
     * @return totalRegistered Total projects registered
     * @return totalVerified Total projects verified
     * @return totalRejected Total projects rejected
     * @return totalPending Total projects pending verification
     */
    function getRegistryStats() external view returns (
        uint256 totalRegistered,
        uint256 totalVerified,
        uint256 totalRejected,
        uint256 totalPending
    ) {
        return (
            totalRegisteredProjects,
            totalVerifiedProjects,
            totalRejectedProjects,
            totalRegisteredProjects - totalVerifiedProjects - totalRejectedProjects
        );
    }

    /**
     * @dev Emergency function to lock a project (in case of disputes)
     * @param projectId Project to lock
     */
    function lockProject(string memory projectId) external onlyOwner projectExists(projectId) {
        projects[projectId].status = ProjectStatus.Locked;
    }

    /**
     * @dev Unlock a previously locked project
     * @param projectId Project to unlock
     */
    function unlockProject(string memory projectId) external onlyOwner projectExists(projectId) {
        require(projects[projectId].status == ProjectStatus.Locked, "CarbonRegistry: Project not locked");
        projects[projectId].status = ProjectStatus.Pending;
    }
}

contracts/NFTCertificate.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

/**
 * @title NFTCertificate
 * @dev NFT contract for Blue Carbon MRV plantation certificates
 * Each verified plantation project gets a unique NFT with metadata
 */
contract NFTCertificate is ERC721, ERC721URIStorage, Ownable {
    using Counters for Counters.Counter;

    Counters.Counter private _tokenIdCounter;

    // Mapping from project ID to token ID
    mapping(string => uint256) public projectToTokenId;

    // Mapping from token ID to project details
    mapping(uint256 => PlantationData) public tokenToProject;

    // Mapping to prevent duplicate project certificates
    mapping(string => bool) public projectCertified;

    struct PlantationData {
        string projectId;
        string location;
        uint256 areaHectares;
        uint256 carbonCredits;
        uint256 plantationDate;
        string ecosystem; // "mangrove", "seagrass", "saltmarsh"
        address beneficiary;
        bool verified;
        bool gisVerified;
        uint256 gisConfidenceScore; // Scaled by 10000 (e.g., 8500 = 0.85)
        string sentinelSceneId;
        uint256 vegetationIndex; // NDVI scaled by 10000
    }

    event CertificateMinted(
        uint256 indexed tokenId,
        string indexed projectId,
        address indexed beneficiary,
        uint256 areaHectares,
        uint256 carbonCredits
    );

    event ProjectVerified(
        string indexed projectId,
        uint256 indexed tokenId,
        address verifier
    );

    constructor() ERC721("Blue Carbon Certificate", "BCC") {}

    /**
     * @dev Mint a new plantation certificate NFT
     * @param projectId Unique project identifier
     * @param location Geographic location of the plantation
     * @param areaHectares Area of plantation in hectares
     * @param carbonCredits Carbon credits generated
     * @param ecosystem Type of blue carbon ecosystem
     * @param beneficiary Address that will receive the NFT
     * @param metadataURI IPFS URI containing certificate metadata
     * @param gisVerified Whether project is verified by satellite imagery
     * @param gisConfidenceScore GIS verification confidence (scaled by 10000)
     * @param sentinelSceneId Sentinel Hub scene identifier
     * @param vegetationIndex NDVI value (scaled by 10000)
     */
    function mintCertificate(
        string memory projectId,
        string memory location,
        uint256 areaHectares,
        uint256 carbonCredits,
        string memory ecosystem,
        address beneficiary,
        string memory metadataURI,
        bool gisVerified,
        uint256 gisConfidenceScore,
        string memory sentinelSceneId,
        uint256 vegetationIndex
    ) public onlyOwner returns (uint256) {
        require(!projectCertified[projectId], "Project already certified");
        require(beneficiary != address(0), "Invalid beneficiary address");
        require(areaHectares > 0, "Area must be greater than 0");

        uint256 tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();

        // Store project data
        tokenToProject[tokenId] = PlantationData({
            projectId: projectId,
            location: location,
            areaHectares: areaHectares,
            carbonCredits: carbonCredits,
            plantationDate: block.timestamp,
            ecosystem: ecosystem,
            beneficiary: beneficiary,
            verified: true,
            gisVerified: gisVerified,
            gisConfidenceScore: gisConfidenceScore,
            sentinelSceneId: sentinelSceneId,
            vegetationIndex: vegetationIndex
        });

        // Update mappings
        projectToTokenId[projectId] = tokenId;
        projectCertified[projectId] = true;

        // Mint the NFT
        _safeMint(beneficiary, tokenId);
        _setTokenURI(tokenId, metadataURI);

        emit CertificateMinted(tokenId, projectId, beneficiary, areaHectares, carbonCredits);

        return tokenId;
    }

    /**
     * @dev Update carbon credits for an existing certificate
     * @param tokenId Token ID of the certificate
     * @param newCarbonCredits Updated carbon credits amount
     */
    function updateCarbonCredits(uint256 tokenId, uint256 newCarbonCredits) public onlyOwner {
        require(_exists(tokenId), "Certificate does not exist");
        tokenToProject[tokenId].carbonCredits = newCarbonCredits;
    }

    /**
     * @dev Get certificate details by project ID
     * @param projectId Project identifier
     * @return PlantationData struct with project details
     */
    function getCertificateByProject(string memory projectId) public view returns (PlantationData memory) {
        require(projectCertified[projectId], "Project not certified");
        uint256 tokenId = projectToTokenId[projectId];
        return tokenToProject[tokenId];
    }

    /**
     * @dev Get certificate details by token ID
     * @param tokenId Token identifier
     * @return PlantationData struct with project details
     */
    function getCertificateByToken(uint256 tokenId) public view returns (PlantationData memory) {
        require(_exists(tokenId), "Certificate does not exist");
        return tokenToProject[tokenId];
    }

    /**
     * @dev Check if a project has been certified
     * @param projectId Project identifier
     * @return bool True if project is certified
     */
    function isProjectCertified(string memory projectId) public view returns (bool) {
        return projectCertified[projectId];
    }

    /**
     * @dev Get total number of certificates minted
     * @return uint256 Total certificates
     */
    function totalCertificates() public view returns (uint256) {
        return _tokenIdCounter.current();
    }

    /**
     * @dev Get all certificates owned by an address
     * @param owner Address to query
     * @return uint256[] Array of token IDs owned
     */
    function getCertificatesByOwner(address owner) public view returns (uint256[] memory) {
        uint256 balance = balanceOf(owner);
        uint256[] memory tokens = new uint256[](balance);
        uint256 currentIndex = 0;

        for (uint256 i = 0; i < _tokenIdCounter.current(); i++) {
            if (_exists(i) && ownerOf(i) == owner) {
                tokens[currentIndex] = i;
                currentIndex++;
            }
        }

        return tokens;
    }

    /**
     * @dev Calculate total verified area for an owner
     * @param owner Address to query
     * @return uint256 Total area in hectares
     */
    function getTotalVerifiedArea(address owner) public view returns (uint256) {
        uint256[] memory tokens = getCertificatesByOwner(owner);
        uint256 totalArea = 0;

        for (uint256 i = 0; i < tokens.length; i++) {
            totalArea += tokenToProject[tokens[i]].areaHectares;
        }

        return totalArea;
    }

    /**
     * @dev Calculate total carbon credits for an owner
     * @param owner Address to query
     * @return uint256 Total carbon credits
     */
    function getTotalCarbonCredits(address owner) public view returns (uint256) {
        uint256[] memory tokens = getCertificatesByOwner(owner);
        uint256 totalCredits = 0;

        for (uint256 i = 0; i < tokens.length; i++) {
            totalCredits += tokenToProject[tokens[i]].carbonCredits;
        }

        return totalCredits;
    }

    // Override required functions
    function _burn(uint256 tokenId) internal override(ERC721, ERC721URIStorage) {
        super._burn(tokenId);

        // Clean up mappings
        string memory projectId = tokenToProject[tokenId].projectId;
        delete tokenToProject[tokenId];
        delete projectToTokenId[projectId];
        projectCertified[projectId] = false;
    }

    function tokenURI(uint256 tokenId) public view override(ERC721, ERC721URIStorage) returns (string memory) {
        return super.tokenURI(tokenId);
    }

    function supportsInterface(bytes4 interfaceId) public view override(ERC721, ERC721URIStorage) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}

scripts/deploy.js.content:
const { ethers } = require("hardhat");

async function main() {
  console.log("Deploying BlueCarbon contract...");

  // Get the ContractFactory and Signers here.
  const [deployer] = await ethers.getSigners();
  console.log("Deploying contracts with the account:", deployer.address);

  // Deploy the contract
  const BlueCarbon = await ethers.getContractFactory("BlueCarbon");
  const blueCarbon = await BlueCarbon.deploy();

  await blueCarbon.waitForDeployment();
  const contractAddress = await blueCarbon.getAddress();

  console.log("BlueCarbon contract deployed to:", contractAddress);

  // Grant some initial roles for testing
  console.log("Setting up initial roles...");

  // Grant admin role to deployer (already has it from constructor)
  console.log("Deployer has admin role");

  // You can grant NGO role to specific addresses here
  // await blueCarbon.grantNGORole("0x...");

  console.log("Deployment completed!");
  console.log(`Contract address: ${contractAddress}`);
  console.log(`Deployer address: ${deployer.address}`);

  // Save deployment info
  const deploymentInfo = {
    contractAddress: contractAddress,
    deployer: deployer.address,
    network: "mumbai",
    timestamp: new Date().toISOString(),
  };

  console.log("Deployment info:", JSON.stringify(deploymentInfo, null, 2));
}

main()
  .then(() => process.exit(0))
  .catch((error) => {
    console.error(error);
    process.exit(1);
  });

