import { ethers, BrowserProvider, Contract, formatEther, parseEther, keccak256, toUtf8Bytes } from "ethers";

// BlueCarbon Token Contract ABI (simplified)
const BLUECARBON_ABI = [
  "function balanceOf(address owner) view returns (uint256)",
  "function mint(address to, uint256 amount) external",
  "function burn(uint256 amount) external",
  "function transfer(address to, uint256 amount) external returns (bool)",
  "function hasRole(bytes32 role, address account) view returns (bool)",
  "function grantRole(bytes32 role, address account) external",
  "event Transfer(address indexed from, address indexed to, uint256 value)",
  "event ProjectVerified(string indexed projectId, uint256 creditsIssued)",
];

// Contract address (will be set after deployment)
const CONTRACT_ADDRESS = import.meta.env.VITE_CONTRACT_ADDRESS as string;

// Role hashes
const ADMIN_ROLE = keccak256(toUtf8Bytes("ADMIN_ROLE"));
const NGO_ROLE = keccak256(toUtf8Bytes("NGO_ROLE"));

export class Web3Service {
  private provider: BrowserProvider;
  private contract: Contract;

  constructor(provider: BrowserProvider) {
    this.provider = provider;
    this.contract = new Contract(CONTRACT_ADDRESS, BLUECARBON_ABI, provider);
  }

  async getCarbonCredits(address: string): Promise<string> {
    try {
      const balance = await this.contract.balanceOf(address);
      return formatEther(balance);
    } catch (error) {
      console.error("Error getting carbon credits:", error);
      return "0";
    }
  }

  async mintCredits(projectId: string, amount: string, recipientAddress: string): Promise<string> {
    try {
      const signer = await this.provider.getSigner();
      const contractWithSigner = this.contract.connect(signer) as any;

      const amountWei = parseEther(amount);
      const tx = await contractWithSigner.mint(recipientAddress, amountWei);

      await tx.wait();
      return tx.hash;
    } catch (error) {
      console.error("Error minting credits:", error);
      throw error;
    }
  }

  async burnCredits(amount: string): Promise<string> {
    try {
      const signer = await this.provider.getSigner();
      const contractWithSigner = this.contract.connect(signer) as any;

      const amountWei = parseEther(amount);
      const tx = await contractWithSigner.burn(amountWei);

      await tx.wait();
      return tx.hash;
    } catch (error) {
      console.error("Error burning credits:", error);
      throw error;
    }
  }

  async transferCredits(to: string, amount: string): Promise<string> {
    try {
      const signer = await this.provider.getSigner();
      const contractWithSigner = this.contract.connect(signer) as any;

      const amountWei = parseEther(amount);
      const tx = await contractWithSigner.transfer(to, amountWei);

      await tx.wait();
      return tx.hash;
    } catch (error) {
      console.error("Error transferring credits:", error);
      throw error;
    }
  }

  async hasRole(role: "admin" | "ngo", address: string): Promise<boolean> {
    try {
      const roleHash = role === "admin" ? ADMIN_ROLE : NGO_ROLE;
      return await this.contract.hasRole(roleHash, address);
    } catch (error) {
      console.error("Error checking role:", error);
      return false;
    }
  }
}

client/src/pages/admin-dashboard.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Clock, CheckCircle, Coins, Globe, Check, X, Eye, FolderOutput } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useWallet } from "@/hooks/use-wallet";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";

export default function AdminDashboard() {
  const { account } = useWallet();
  const { toast } = useToast();
  const [verifyingProject, setVerifyingProject] = useState<string | null>(null);
  const [carbonCredits, setCarbonCredits] = useState("");

  // Get pending projects
  const { data: pendingData, isLoading: loadingPending } = useQuery({
    queryKey: ["/api/projects", "pending"],
    enabled: !!account,
  });

  // Get all projects for stats
  const { data: allProjectsData } = useQuery({
    queryKey: ["/api/projects"],
    enabled: !!account,
  });

  const projects = (allProjectsData as any)?.projects || [];
  const pendingProjects = (pendingData as any)?.projects || [];
  const verifiedProjects = projects.filter((p: any) => p.status === "verified");
  const totalCredits = verifiedProjects.reduce((sum: number, p: any) => sum + parseFloat(p.carbonCredits || "0"), 0);
  const totalArea = verifiedProjects.reduce((sum: number, p: any) => sum + parseFloat(p.areaHectares || "0"), 0);

  const verifyProjectMutation = useMutation({
    mutationFn: ({ projectId, status, credits }: { projectId: string; status: "verified" | "rejected"; credits?: string }) =>
      apiRequest("POST", `/api/projects/${projectId}/verify`, {
        status,
        verifiedBy: account,
        carbonCredits: credits,
        blockchainTxHash: `0x${Math.random().toString(16).substr(2, 64)}`, // Mock transaction hash
      }),
    onSuccess: (_, variables) => {
      toast({
        title: variables.status === "verified" ? "Project verified successfully!" : "Project rejected",
        description: variables.status === "verified" ? `Carbon credits will be minted.` : "The project has been rejected.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/projects"] });
      setVerifyingProject(null);
      setCarbonCredits("");
    },
    onError: (error: any) => {
      toast({
        title: "Error processing project",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleVerify = (projectId: string) => {
    if (!carbonCredits) {
      toast({
        title: "Carbon credits required",
        description: "Please enter the number of carbon credits to mint.",
        variant: "destructive",
      });
      return;
    }
    verifyProjectMutation.mutate({ projectId, status: "verified", credits: carbonCredits });
  };

  const handleReject = (projectId: string) => {
    verifyProjectMutation.mutate({ projectId, status: "rejected" });
  };

  if (!account) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <Card className="w-full max-w-md">
          <CardContent className="pt-6 text-center">
            <h2 className="text-2xl font-bold mb-4">Connect Your Wallet</h2>
            <p className="text-muted-foreground mb-4">
              Please connect your wallet to access the admin dashboard.
            </p>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="py-12 bg-muted/30 min-h-screen">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-3xl font-bold">Admin Dashboard</h1>
            <p className="text-muted-foreground mt-2">Review and verify blue carbon projects</p>
          </div>
          <Button variant="outline" data-testid="button-export-report">
            <FolderOutput className="mr-2 h-4 w-4" />
            Export Report
          </Button>
        </div>

        {/* Admin Stats */}
        <div className="grid md:grid-cols-4 gap-6 mb-8">
          <Card data-testid="stat-pending-review">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-muted-foreground text-sm">Pending Review</p>
                  <p className="text-2xl font-bold text-secondary">{pendingProjects.length}</p>
                </div>
                <Clock className="text-secondary text-2xl" />
              </div>
            </CardContent>
          </Card>
          <Card data-testid="stat-verified-projects">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-muted-foreground text-sm">Verified Projects</p>
                  <p className="text-2xl font-bold text-primary">{verifiedProjects.length}</p>
                </div>
                <CheckCircle className="text-primary text-2xl" />
              </div>
            </CardContent>
          </Card>
          <Card data-testid="stat-credits-minted">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-muted-foreground text-sm">Credits Minted</p>
                  <p className="text-2xl font-bold text-primary">{totalCredits.toFixed(0)}</p>
                </div>
                <Coins className="text-primary text-2xl" />
              </div>
            </CardContent>
          </Card>
          <Card data-testid="stat-total-area">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-muted-foreground text-sm">Total Area</p>
                  <p className="text-2xl font-bold text-secondary">{totalArea.toFixed(0)} ha</p>
                </div>
                <Globe className="text-secondary text-2xl" />
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Project Review Queue */}
        <Card data-testid="project-review-queue">
          <CardHeader>
            <CardTitle>Project Review Queue</CardTitle>
            <p className="text-muted-foreground">Review and verify submitted projects</p>
          </CardHeader>
          <CardContent>
            {loadingPending ? (
              <div className="space-y-6">
                {[1, 2].map(i => (
                  <div key={i} className="border-b border-border pb-6 animate-pulse">
                    <div className="h-6 bg-muted rounded w-1/3 mb-4"></div>
                    <div className="h-4 bg-muted rounded w-full mb-2"></div>
                    <div className="h-4 bg-muted rounded w-2/3"></div>
                  </div>
                ))}
              </div>
            ) : pendingProjects.length === 0 ? (
              <div className="text-center py-8 text-muted-foreground">
                <CheckCircle className="mx-auto h-12 w-12 mb-4" />
                <p>No projects pending review.</p>
                <p className="text-sm">All submitted projects have been processed.</p>
              </div>
            ) : (
              <div className="divide-y divide-border">
                {pendingProjects.map((project: any) => (
                  <div key={project.id} className="py-6" data-testid={`project-review-${project.id}`}>
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center space-x-3 mb-3">
                          <h4 className="font-semibold">{project.name}</h4>
                          <Badge variant="secondary">High Priority</Badge>
                        </div>
                        <div className="grid md:grid-cols-2 gap-4 mb-4">
                          <div>
                            <p className="text-sm text-muted-foreground">
                              Location: <span className="font-medium">{project.location} ({project.latitude}, {project.longitude})</span>
                            </p>
                            <p className="text-sm text-muted-foreground">
                              Area: <span className="font-medium">{project.areaHectares} hectares</span>
                            </p>
                            <p className="text-sm text-muted-foreground">
                              Type: <span className="font-medium">{project.projectType}</span>
                            </p>
                          </div>
                          <div>
                            <p className="text-sm text-muted-foreground">
                              Submitted: <span className="font-medium">{new Date(project.createdAt).toLocaleDateString()}</span>
                            </p>
                            <p className="text-sm text-muted-foreground mb-2">
                              Description: <span className="font-medium">{project.description}</span>
                            </p>
                          </div>
                        </div>
                      </div>
                      <div className="flex flex-col space-y-2 ml-6">
                        <Dialog>
                          <DialogTrigger asChild>
                            <Button 
                              className="bg-primary text-primary-foreground hover:bg-primary/90"
                              data-testid={`button-approve-${project.id}`}
                            >
                              <Check className="mr-2 h-4 w-4" />
                              Approve & Mint
                            </Button>
                          </DialogTrigger>
                          <DialogContent>
                            <DialogHeader>
                              <DialogTitle>Verify Project & Mint Credits</DialogTitle>
                            </DialogHeader>
                            <div className="space-y-4">
                              <div>
                                <Label htmlFor="carbon-credits">Carbon Credits to Mint</Label>
                                <Input
                                  id="carbon-credits"
                                  type="number"
                                  placeholder="Enter carbon credits amount"
                                  value={carbonCredits}
                                  onChange={(e) => setCarbonCredits(e.target.value)}
                                  data-testid="input-carbon-credits"
                                />
                              </div>
                              <div className="flex space-x-2">
                                <Button 
                                  onClick={() => handleVerify(project.id)}
                                  disabled={verifyProjectMutation.isPending}
                                  data-testid="button-confirm-verify"
                                >
                                  {verifyProjectMutation.isPending ? "Processing..." : "Verify & Mint"}
                                </Button>
                                <DialogTrigger asChild>
                                  <Button variant="outline">Cancel</Button>
                                </DialogTrigger>
                              </div>
                            </div>
                          </DialogContent>
                        </Dialog>

                        <Button 
                          variant="destructive"
                          onClick={() => handleReject(project.id)}
                          disabled={verifyProjectMutation.isPending}
                          data-testid={`button-reject-${project.id}`}
                        >
                          <X className="mr-2 h-4 w-4" />
                          Reject
                        </Button>

                        <Button variant="outline" data-testid={`button-review-${project.id}`}>
                          <Eye className="mr-2 h-4 w-4" />
                          Review
                        </Button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/industry-dashboard.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { toast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { ShoppingCart, Award, TrendingUp, Leaf, FileText, Download } from "lucide-react";
import { useState } from "react";

interface CarbonCredit {
  id: string;
  projectId: string;
  projectName: string;
  location: string;
  ecosystem: string;
  areaHectares: number;
  creditsAvailable: number;
  pricePerCredit: number;
  nftTokenId?: number;
  verifiedAt: string;
  submitterOrg: string;
}

interface PurchaseHistory {
  id: string;
  projectId: string;
  projectName: string;
  creditsRetired: number;
  retirementReason: string;
  retiredAt: string;
  nftCertificateUrl?: string;
}

export default function IndustryDashboard() {
  const [selectedCredit, setSelectedCredit] = useState<CarbonCredit | null>(null);
  const [purchaseAmount, setPurchaseAmount] = useState(0);

  // Fetch available carbon credits
  const { data: availableCredits = [], isLoading: loadingCredits } = useQuery<CarbonCredit[]>({
    queryKey: ["/api/credits/marketplace"],
  });

  // Fetch purchase history
  const { data: purchaseHistory = [], isLoading: loadingHistory } = useQuery<PurchaseHistory[]>({
    queryKey: ["/api/credits/purchase-history"],
  });

  // Fetch industry statistics
  const { data: stats = {} } = useQuery<{
    totalCreditsPurchased?: number;
    monthlyGrowth?: number;
    totalCO2Offset?: number;
    projectsSupported?: number;
    countries?: number;
    nftCertificates?: number;
  }>({
    queryKey: ["/api/industry/stats"],
  });

  // Purchase credits mutation
  const purchaseMutation = useMutation({
    mutationFn: async (data: { projectId: string; credits: number; retirementReason: string }) => {
      const response = await fetch("/api/credits/purchase", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("Purchase failed");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/credits/marketplace"] });
      queryClient.invalidateQueries({ queryKey: ["/api/credits/purchase-history"] });
      queryClient.invalidateQueries({ queryKey: ["/api/industry/stats"] });
      toast({
        title: "Credits Purchased Successfully",
        description: "Your carbon credits have been retired and NFT certificate generated.",
      });
      setSelectedCredit(null);
      setPurchaseAmount(0);
    },
    onError: (error: any) => {
      toast({
        title: "Purchase Failed",
        description: error.message || "Failed to purchase carbon credits",
        variant: "destructive",
      });
    },
  });

  const handlePurchase = (retirementReason: string) => {
    if (!selectedCredit || purchaseAmount <= 0) return;

    purchaseMutation.mutate({
      projectId: selectedCredit.projectId,
      credits: purchaseAmount,
      retirementReason,
    });
  };

  return (
    <div className="container mx-auto p-6 space-y-8" data-testid="industry-dashboard">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Industry Dashboard</h1>
          <p className="text-gray-600 dark:text-gray-300">Purchase verified carbon credits to offset your emissions</p>
        </div>
        <Badge variant="outline" className="text-lg px-4 py-2">
          <Leaf className="w-4 h-4 mr-2" />
          Corporate Buyer
        </Badge>
      </div>

      {/* Statistics Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Credits Purchased</CardTitle>
            <ShoppingCart className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="total-credits-purchased">
              {stats.totalCreditsPurchased?.toLocaleString() || 0}
            </div>
            <p className="text-xs text-muted-foreground">
              +{stats.monthlyGrowth || 0}% from last month
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">CO2 Offset (tons)</CardTitle>
            <TrendingUp className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="co2-offset">
              {stats.totalCO2Offset?.toLocaleString() || 0}
            </div>
            <p className="text-xs text-muted-foreground">
              Lifetime carbon footprint reduction
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Projects Supported</CardTitle>
            <Award className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="projects-supported">
              {stats.projectsSupported || 0}
            </div>
            <p className="text-xs text-muted-foreground">
              Across {stats.countries || 0} countries
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">NFT Certificates</CardTitle>
            <FileText className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="nft-certificates">
              {stats.nftCertificates || 0}
            </div>
            <p className="text-xs text-muted-foreground">
              Proof of offset certificates owned
            </p>
          </CardContent>
        </Card>
      </div>

      <Tabs defaultValue="marketplace" className="w-full">
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="marketplace" data-testid="tab-marketplace">Marketplace</TabsTrigger>
          <TabsTrigger value="history" data-testid="tab-history">Purchase History</TabsTrigger>
        </TabsList>

        <TabsContent value="marketplace" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Available Carbon Credits</CardTitle>
              <CardDescription>
                Purchase verified blue carbon credits from restoration projects
              </CardDescription>
            </CardHeader>
            <CardContent>
              {loadingCredits ? (
                <div className="text-center py-8">Loading available credits...</div>
              ) : (
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Project</TableHead>
                      <TableHead>Location</TableHead>
                      <TableHead>Ecosystem</TableHead>
                      <TableHead>Area (ha)</TableHead>
                      <TableHead>Credits Available</TableHead>
                      <TableHead>Price per Credit</TableHead>
                      <TableHead>Action</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {availableCredits.map((credit) => (
                      <TableRow key={credit.id}>
                        <TableCell className="font-medium">{credit.projectName}</TableCell>
                        <TableCell>{credit.location}</TableCell>
                        <TableCell>
                          <Badge variant="secondary">{credit.ecosystem}</Badge>
                        </TableCell>
                        <TableCell>{credit.areaHectares}</TableCell>
                        <TableCell>{credit.creditsAvailable}</TableCell>
                        <TableCell>${credit.pricePerCredit}</TableCell>
                        <TableCell>
                          <Dialog>
                            <DialogTrigger asChild>
                              <Button 
                                size="sm" 
                                onClick={() => setSelectedCredit(credit)}
                                data-testid={`button-purchase-${credit.projectId}`}
                              >
                                Purchase
                              </Button>
                            </DialogTrigger>
                            <DialogContent>
                              <DialogHeader>
                                <DialogTitle>Purchase Carbon Credits</DialogTitle>
                                <DialogDescription>
                                  Purchase credits from {credit.projectName} to offset your emissions
                                </DialogDescription>
                              </DialogHeader>
                              <div className="space-y-4">
                                <div>
                                  <label className="text-sm font-medium">Number of Credits</label>
                                  <input
                                    type="number"
                                    className="w-full mt-1 p-2 border rounded"
                                    max={credit.creditsAvailable}
                                    min="1"
                                    value={purchaseAmount}
                                    onChange={(e) => setPurchaseAmount(parseInt(e.target.value))}
                                    data-testid="input-purchase-amount"
                                  />
                                  <p className="text-xs text-gray-500 mt-1">
                                    Total: ${(purchaseAmount * credit.pricePerCredit).toFixed(2)}
                                  </p>
                                </div>
                                <div>
                                  <label className="text-sm font-medium">Retirement Reason</label>
                                  <select 
                                    className="w-full mt-1 p-2 border rounded"
                                    onChange={(e) => handlePurchase(e.target.value)}
                                    data-testid="select-retirement-reason"
                                  >
                                    <option value="">Select reason...</option>
                                    <option value="Annual carbon offset program">Annual carbon offset program</option>
                                    <option value="Event carbon neutrality">Event carbon neutrality</option>
                                    <option value="Corporate sustainability goals">Corporate sustainability goals</option>
                                    <option value="Product lifecycle offset">Product lifecycle offset</option>
                                    <option value="Voluntary climate action">Voluntary climate action</option>
                                  </select>
                                </div>
                              </div>
                            </DialogContent>
                          </Dialog>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="history" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Purchase History</CardTitle>
              <CardDescription>
                View your carbon credit purchases and offset certificates
              </CardDescription>
            </CardHeader>
            <CardContent>
              {loadingHistory ? (
                <div className="text-center py-8">Loading purchase history...</div>
              ) : (
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Project</TableHead>
                      <TableHead>Credits Retired</TableHead>
                      <TableHead>Retirement Reason</TableHead>
                      <TableHead>Date</TableHead>
                      <TableHead>Certificate</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {purchaseHistory.map((purchase) => (
                      <TableRow key={purchase.id}>
                        <TableCell className="font-medium">{purchase.projectName}</TableCell>
                        <TableCell>{purchase.creditsRetired}</TableCell>
                        <TableCell>{purchase.retirementReason}</TableCell>
                        <TableCell>{new Date(purchase.retiredAt).toLocaleDateString()}</TableCell>
                        <TableCell>
                          {purchase.nftCertificateUrl ? (
                            <Button 
                              size="sm" 
                              variant="outline"
                              data-testid={`button-download-certificate-${purchase.id}`}
                            >
                              <Download className="w-4 h-4 mr-2" />
                              Download
                            </Button>
                          ) : (
                            <Badge variant="secondary">Processing</Badge>
                          )}
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

